import express from "express";
import { signup, login, logout, updateProfile, checkAuth } from "../controllers/auth.controller";
import { isProtected } from "../middleware/auth.middleware";
const router = express.Router();
router.post("/signup", signup);
router.post("/login", login);
router.post("/logout", logout);
router.put("/update-profile", isProtected, updateProfile);
router.get("/check", isProtected, checkAuth);
export default router;
